import React, { useState } from 'react';

import { Header1 } from './Elements/Header1';

import './App.css';
import { Header2 } from './Elements/Header2';

function App() {
  const [hexColor, setHexColor] = useState("");
  const [rgbColor, setRgbColor] = useState("#ffffff");

  const [chosenColors, setChosenColors] = useState<string[]>(["#FFFFFF", "#000000"]);

  const onHexChange = (e: any) => {
    const input = e.currentTarget.value;
    if (/^[0-9a-f]+$/.test(input) || input === "" && input.length < 6) {
      setHexColor(input);
    }
  };

  const removeColor = (color: string) => {
    if (chosenColors.includes(color)) {
      let filteredColors = chosenColors.filter((c) => c !== color)
      setChosenColors(filteredColors)
    }
  }

  const handleSubmit = (e: any) => {
    e.preventDefault()

    console.log('Form Submitted!')
    if (hexColor && hexColor !== "") {
      setChosenColors(chosenColors => [...chosenColors, `#${hexColor.toLocaleUpperCase()}`])
      setHexColor("")
    }

  }

  return (
    <main className="font-mono absolute w-full h-screen flex flex-col overflow-hidden">
      <Header1
        title="Chromagraph"
      />

      <section className='flex-1 flex justify-between gap-4 px-20 lg:px-40'>
        <section className="flex flex-col flex-1">
          <form onSubmit={handleSubmit}
            className="flex flex-col gap-4"
          >
            <Header2
              title={'Select a Color'}
              alignment='start'
            />

            <div className="flex flex-row gap-4">
              <div className="flex flex-1 basis-1 gap-4 flex-col">
                <label htmlFor="colorPicker">Hex Selector:</label>
                <input
                  type="input"
                  name="colorPicker"
                  value={hexColor}
                  onChange={onHexChange}
                  maxLength={6}
                  className='border-black border-2 py-2 px-4 rounded-md'
                />

                <label htmlFor="colorPicker">Color Picker:</label>
                <input name="colorPicker" type="color"
                  className='border-black border-2 rounded-md'
                />
              </div>

              <div className="flex flex-1 basis-2/3 bg-primary w-40 rounded-md" />
            </div>


            <input type="submit" value="Add Color"
              className="outline-black outline outline-4 py-4 rounded-md hover:cursor-pointer uppercase hover:outline-8"
            />
          </form>

          <Header2
            title="Manage Colors"
            alignment='start'
          />

          <ul className="flex flex-col gap-4">
            {chosenColors.map((color, i) => (
              <li key={i}
                className='flex flex-row gap-4 items-center'
              >
                <button onClick={() => removeColor(color)}
                  className="outline outline-4 p-4 rounded-md hover:cursor-pointer hover:outline-8"
                >
                  X
                </button>

                <p>
                  {color}
                </p>
              </li>
            ))}
          </ul>
        </section>

        <section className="flex flex-col">
          <Header2
            title="Color Combos"
            alignment='end'
          />

          <div className="overflow-scroll w-full border-8 border-black rounded-md">
            <table className='flex flex-col w-full'>
              <tr className="flex flex-row w-full">
                <th className="flex-1 p-4 w-full items-center outline outline-2 outline-black bg-black text-white">
                  Colors
                </th>
                {chosenColors.map((color, i) => (
                  <th key={`foreground-${i}`}
                    className={`flex-1 p-4 w-full items-center outline outline-2 outline-black bg-[${color ? color : '#000000'}]`}
                  >
                    {color}
                  </th>
                ))}
              </tr>

              {chosenColors.map((color, i) => (
                <tr key={`background-${i}`}
                  className="flex flex-row w-full"
                >
                  <td className="flex-1 p-4 w-full text-center outline outline-2 outline-black">
                    {color}
                  </td>
                  {chosenColors.map((c, j) => (
                    <td className={`flex-1 p-4 w-full text-center outline outline-2 outline-black ${color === c && 'bg-black'}`}>
                      {c}/{color}
                    </td>
                  ))}
                </tr>
              ))}
            </table>
          </div>
        </section>
      </section>
    </main>
  );
}

export default App;
