import React, { useEffect, useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
  import { faHashtag, faClose, faCheckCircle } from '@fortawesome/free-solid-svg-icons'

import { Header1 } from './Elements/Header1';

import './App.css';
import { Header2 } from './Elements/Header2';
import { Header3 } from './Elements/Header3';

function App() {
  const [hexColor, setHexColor] = useState("");
  const [highlight, setHighlight] = useState(false);

  const [chosenColors, setChosenColors] = useState<string[]>(["#FFFFFF", "#000000"]);

  const onHexChange = (e: any) => {
    const input = e.currentTarget.value;
    if (/^[0-9a-f]+$/.test(input) || input === "") {
      setHexColor(input);
    }
  };

  const activateHighlight = (color: string) => {
    setHighlight(true);
  }

  const removeColor = (color: string) => {
    if (chosenColors.includes(color)) {
      let filteredColors = chosenColors.filter((c) => c !== color)
      setChosenColors(filteredColors)
    }
  }

  const compareColors = (color1: string, color2: string) => {
    return (
      <div className='w-full h-full flex-1 flex justify-center items-center'>
        {color1 === color2 ? (
          <div className="w-20 h-20 flex justify-center items-center">
            <p>---</p>
          </div>
        ) : (
          <div style={{backgroundColor: color2}}
            className='w-20 h-20 flex flex-col p-2 justify-center items-start rounded-xl'
          >
            <div style={{color: color1}} className='flex flex-row items-center gap-4'>
              <p className='font-bold'>
                TTL:
              </p>
              <FontAwesomeIcon
                icon={faCheckCircle}
              />
            </div>

            <p style={{color: color1}}>Txt:</p>
            <p style={{color: color1}}>Title</p>
          </div>
        )}
      </div>
    )
  }

  const handleSubmit = (e: any) => {
    e.preventDefault()

    console.log('Form Submitted!')
    if (hexColor && hexColor !== "" && hexColor.length === 6) {
      setChosenColors(chosenColors => [...chosenColors, `#${hexColor.toLocaleUpperCase()}`])
      setHexColor("")
    } 
  }

  useEffect(() => {
    console.log('hex color changed')
  }, [hexColor])

  return (
    <main className="font-mono absolute w-full h-screen flex flex-col overflow-hidden">
      <Header1
        title="Chromagraph"
      />

      <section className='flex flex-row gap-4 w-full px-4 md:px-20 xl:px-40 border-b-2'>
        <form onSubmit={handleSubmit}
          className="flex flex-row py-4 justify-between"
        >
          <div className="flex flex-row gap-4 items-center">
            <div className="flex flex-col justify-center items-center gap-4">
              <Header2
                title='Select a Color'
                alignment='center'
              />

              <div
                className='h-40 w-40 border-black border-2 rounded-full'
              />

              <input type="submit" value="Add Color"
                className="uppercase w-full bg-black p-4 rounded-full text-white hover:cursor-pointer hover:outline-4 hover:outline-black hover:outline"
              />
            </div>

            <div className="flex flex-1 gap-4 flex-col">
              <label htmlFor="colorPicker" className='text-left'>Hex Selector:</label>
              <div className="relative">
                <FontAwesomeIcon
                  icon={faHashtag}
                  className='absolute top-1/2 left-1 border-black border-2 rounded-full h-4 w-4 p-2 transform -translate-y-1/2'
                />
                <input
                  type="input"
                  name="colorPicker"
                  value={hexColor}
                  onChange={onHexChange}
                  autoFocus={true}
                  maxLength={6}
                  className='border-black border-2 py-2 pl-12 rounded-full w-full focus:outline-2 focus:outline-black focus:outline'
                />
              </div>
            </div>
          </div>
        </form>
      </section>

      <section className='flex-1 flex flex-row w-full bg-gray'>
        <div className="flex flex-col border-r-2 h-full ">
          <div className="sticky top-0 w-full ">
            <Header3
              title="Manage Colors"
              alignment='start'
            />
          </div>

          <ul className="flex flex-col h-full gap-4 py-4 pr-4 pl-4 md:pl-20 xl:pl-40 overflow-y-scroll">
            {chosenColors.map((color, i) => (
              <li key={`colorBG-${i}`}
                className='flex justify-between flex-row gap-4 p-2 items-center h-20 w-full rounded-full border-2'
              >
                <button
                  onClick={() => removeColor(color)}
                  className='text-white bg-black p-2 rounded-full h-10 w-10 hover:outline-4 hover:outline-black hover:outline'
                >
                  <FontAwesomeIcon 
                    icon={faClose}
                  />
                </button>

                <p className="text-xs font-bold">
                  {color}
                </p>

                <div
                  style={{backgroundColor: color}}
                  className={`w-10 h-10 rounded-full border-black border-2`}
                />
              </li>
            ))}
          </ul>
        </div>

        <div className="flex flex-col w-full ">
          <div
            className='flex w-full h-16 border-b-2'
          >
            <ul className="flex flex-row gap-4 px-4">
              {chosenColors.map((color, i) => (
                <li key={`colorFG-${i}`}
                  className='flex flex-col h-16 justify-center items-center'
                >
                  <button style={{backgroundColor: color}}
                    className='w-10 h-10 rounded-full border-2'
                    onClick={() => removeColor(color)}
                    onMouseOver={() => activateHighlight(color)}
                  >
                    {highlight}
                    <FontAwesomeIcon
                      icon={faClose}
                      className='hidden hover:inline'
                    />
                  </button>
                </li>
              ))}
            </ul>
          </div>

        </div>

        {/* <table className='flex flex-col w-full'>
          <tr className="flex flex-row w-full">
            <th className="flex-1 p-4 w-full min-w-[200px]">
              <Header3
                title="Manage Colors"
                alignment='start'
              />
            </th>
            {chosenColors.map((color, i) => (
              <th key={`foreground-${i}`}
                className={`flex-1 flex p-4 w-40 justify-center items-center align-middle`}
              >
                <div
                  style={{backgroundColor: color}}
                  className="h-10 w-10 outline rounded-full"
                />
              </th>
            ))}
          </tr>

          {chosenColors.map((color, i) => (
            <tr key={`background-${i}`}
              className="flex flex-row w-full"
            >
              <td className="flex-1 py-4 pr-4 w-full min-w-[200px] text-center">
                <div
                  className='flex justify-between flex-row gap-4 p-2 items-center w-full rounded-full border-2'
                >
                  <button
                    onClick={() => removeColor(color)}
                    className='text-white bg-black p-2 rounded-full h-10 w-10 hover:outline-4 hover:outline-black hover:outline'
                  >
                    <FontAwesomeIcon 
                      icon={faClose}
                    />
                  </button>

                  <p className="text-xs font-bold">
                    {color}
                  </p>

                  <div
                    style={{backgroundColor: color}}
                    className={`w-10 h-10 rounded-full border-black border-2`}
                  />
                </div>
              </td>
              {chosenColors.map((c, j) => (
                <td className={`flex-1 w-40 text-center`}>
                  {compareColors(c, color)}
                </td>
              ))}
            </tr>
          ))}
        </table> */}
      </section>
    </main>
  );
}

export default App;
