import React, { useEffect, useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
  import { faHashtag, faCopy, faClipboard, faClose,faTriangleCircleSquare, faCheckCircle } from '@fortawesome/free-solid-svg-icons'

import { Header1 } from './Elements/Header1';

import './App.css';
import { Header2 } from './Elements/Header2';
import { Header3 } from './Elements/Header3';

function App() {
  const [hexColor, setHexColor] = useState("");

  const [chosenColors, setChosenColors] = useState<string[]>([
    "#FFFFFF",
    "#FF0000",
    "#00FF00",
    "#0000FF",
    "#000000"
  ]);

  const onHexChange = (e: any) => {
    const input = e.currentTarget.value;
    if (/^[0-9a-f]+$/.test(input) || input === "") {
      setHexColor(input);
    }
  };

  const removeColor = (color: string) => {
    if (chosenColors.includes(color)) {
      let filteredColors = chosenColors.filter((c) => c !== color)
      setChosenColors(filteredColors)
    }
  }

  const compareColors = (color1: string, color2: string) => {
    let titleRate: string;
    let textRate: string;
    let guiRate: string;

    fetch("https://api.example.com/items")
      .then(res => res.json())
      .then(
        (result) => {
          this.setState({
            isLoaded: true,
            items: result.items
          });
        },
        // Note: it's important to handle errors here
        // instead of a catch() block so that we don't swallow
        // exceptions from actual bugs in components.
        (error) => {
          this.setState({
            isLoaded: true,
            error
          });
        }
      )

    return (
      <div className='w-full h-full flex-1 flex justify-center items-center'>
        {color1 === color2 ? (
          <div className="w-20 h-20 flex justify-center items-center">
            <FontAwesomeIcon
              icon={faCopy}
              className='w-10 h-10 text-gray'
              onClick={() => {navigator.clipboard.writeText(color1)}}
            />
          </div>
        ) : (
          <div style={{backgroundColor: color2}}
            className='w-20 h-20 flex flex-col p-2 justify-center items-start rounded-xl'
          >
            <div style={{color: color1}} className='flex flex-row w-full justify-start items-center gap-2'>
              <p className='font-bold'>
                T:
              </p>
              <FontAwesomeIcon
                icon={faCheckCircle}
              />
            </div>

            <div style={{color: color1}} className='flex flex-row w-full justify-start items-center gap-2'>
              <p>
                t:
              </p>
              <FontAwesomeIcon
                icon={faCheckCircle}
              />
            </div>

            <div style={{color: color1}} className='flex flex-row w-full justify-start items-center gap-2'>
              <p>
                <FontAwesomeIcon
                  icon={faTriangleCircleSquare}
                />:
              </p>
              <FontAwesomeIcon
                icon={faCheckCircle}
              />
            </div>
          </div>
        )}
      </div>
    )
  }

  const handleSubmit = (e: any) => {
    e.preventDefault()

    console.log('Form Submitted!')
    if (hexColor && hexColor !== "" && hexColor.length === 6) {
      setChosenColors(chosenColors => [...chosenColors, `#${hexColor.toLocaleUpperCase()}`])
      setHexColor("")
    } 
  }

  useEffect(() => {
    console.log('hex color changed')
  }, [hexColor])

  return (
    <main className="font-mono absolute w-full h-screen flex flex-col overflow-hidden">
      <Header1
        title="Chromagram"
      />

      <section className='flex flex-row gap-4 w-full px-4 md:px-20 xl:px-40 border-b-2'>
        <form onSubmit={handleSubmit}
          className="flex flex-row py-4 justify-between"
        >
          <div className="flex flex-row gap-4 items-center">
            <div className="flex flex-col justify-center items-center gap-4">
              <Header2
                title='Select a Color'
                alignment='center'
              />

              <div
                className='h-40 w-40 border-black border-2 rounded-full'
              />

              <input type="submit" value="Add Color"
                className="uppercase w-full bg-black p-4 rounded-full text-white hover:cursor-pointer hover:outline-4 hover:outline-black hover:outline focus:outline-4"
              />
            </div>

            <div className="flex flex-1 gap-4 flex-col">
              <label htmlFor="colorPicker" className='text-left'>Hex Selector:</label>
              <div className="relative">
                <FontAwesomeIcon
                  icon={faHashtag}
                  className='absolute top-1/2 left-1 text-gray rounded-full h-4 w-4 p-2 transform -translate-y-1/2'
                />
                <input
                  type="input"
                  name="colorPicker"
                  value={hexColor}
                  onChange={onHexChange}
                  autoFocus={true}
                  maxLength={6}
                  className='border-black border-2 py-2 pl-8 rounded-full w-full focus:outline-2 focus:outline-black focus:outline'
                />
              </div>
            </div>
          </div>
        </form>
      </section>

      <section className='flex-1 flex relative flex-row w-full overflow-scroll'>
        <div className={`z-10 flex sticky left-0 flex-col border-r-2 ${chosenColors.length > 3 ? 'h-fit' : 'h-full'}`}>
          <div className="sticky top-0 w-full">
            <Header3
              title="Manage Colors"
              alignment='start'
              padding={chosenColors.length > 0}
            />
          </div>

          <ul className="flex flex-col h-full gap-4 py-4 pr-4 pl-4 md:pl-20 xl:pl-40 items-center bg-white">
            {chosenColors.map((color, i) => (
              <li key={`colorBG-${i}`}
                className='flex justify-center md:justify-between flex-row gap-4 p-2 items-center h-20 w-20 md:w-full rounded-full md:border-2'
              >
                <button
                  onClick={() => removeColor(color)}
                  className='hidden md:inline text-white bg-black p-2 rounded-full h-10 w-10 hover:outline-4 hover:outline-black hover:outline'
                >
                  <FontAwesomeIcon 
                    icon={faClose}
                  />
                </button>

                <p className="hidden md:inline text-xs font-bold">
                  {color}
                </p>

                <div
                  style={{backgroundColor: color}}
                  className={`hidden md:flex w-10 h-10 rounded-full border-black border-2`}
                />

                <button
                  style={{backgroundColor: color}}
                  className={`md:hidden w-10 h-10 rounded-full border-black border-2`}
                  onClick={() => removeColor(color)}
                />
              </li>
            ))}
          </ul>
        </div>

        <ul className={`flex flex-row relative border-r-2 ${chosenColors.length > 3 ? 'h-fit' : 'h-full'} pb-2`}>
          {chosenColors.map((color1, i) => (
            <li key={`colorFG-${i}`}
              className='flex flex-col relative justify-start items-center gap-6'
            >
              <button
                  onClick={() => removeColor(color1)}
                  className='w-full sticky top-0 border-b-2 py-4 flex justify-center items-center bg-white'
                >
                  <div style={{backgroundColor: color1}}
                    className='w-10 h-10 rounded-full border-2'
                  >

                  </div>
              </button>

              <ul className="flex flex-col -mt-2 gap-4 px-4">
                {chosenColors.map((color2, j) => (
                  <li key={`colorSet-${i}-${j}`}>
                    {compareColors(color1, color2)}
                  </li>
                ))}
              </ul>
            </li>

          ))}
        </ul>

        <section className="flex sticky top-0 right-0 bg-gray w-full">
          <div className="fixed w-full h-full">
            <Header3
              title='How to Use:'
              alignment='start'
            />
          </div>
        </section>
      </section>
    </main>
  );
}

export default App;
