import React, { useEffect, useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
  import { faHashtag, faClose } from '@fortawesome/free-solid-svg-icons'

import { Header1 } from './Elements/Header1';

import './App.css';
import { Header2 } from './Elements/Header2';

function App() {
  const [hexColor, setHexColor] = useState("");

  const [chosenColors, setChosenColors] = useState<string[]>(["#FFFFFF", "#000000"]);

  const onHexChange = (e: any) => {
    const input = e.currentTarget.value;
    if (/^[0-9a-f]+$/.test(input) || input === "") {
      setHexColor(input);
    }
  };

  const removeColor = (color: string) => {
    if (chosenColors.includes(color)) {
      let filteredColors = chosenColors.filter((c) => c !== color)
      setChosenColors(filteredColors)
    }
  }

  const handleSubmit = (e: any) => {
    e.preventDefault()

    console.log('Form Submitted!')
    if (hexColor && hexColor !== "" && hexColor.length === 6) {
      setChosenColors(chosenColors => [...chosenColors, `#${hexColor.toLocaleUpperCase()}`])
      setHexColor("")
    } 
  }

  useEffect(() => {
    console.log('hex color changed')
  }, [hexColor])

  return (
    <main className="font-mono absolute w-full h-screen flex flex-col">
      <Header1
        title="Chromagraph"
      />

      <section className='flex flex-row gap-4 w-full px-20 lg:px-40 bg-primary'>
        <form onSubmit={handleSubmit}
          className="flex flex-row py-4 justify-between"
        >
          <div className="flex flex-row gap-4">
            <div className="flex flex-col gap-4">
              <Header2
                title='Select a Color'
                alignment='start'
              />

              <div
                className='h-40 w-40 border-black border-2 rounded-full'
              />

              <input type="submit" value="Add Color"
                className="uppercase bg-black p-4 rounded-full text-white hover:cursor-pointer hover:outline-4"
              />
            </div>

            <div className="flex flex-1 gap-4 flex-col">
              <label htmlFor="colorPicker" className='text-left'>Hex Selector:</label>
              <div className="relative">
                <FontAwesomeIcon
                  icon={faHashtag}
                  className='absolute top-1/2 left-1 border-black border-2 rounded-full h-4 w-4 p-2 transform -translate-y-1/2'
                />
                <input
                  type="input"
                  name="colorPicker"
                  value={hexColor}
                  onChange={onHexChange}
                  autoFocus={true}
                  maxLength={6}
                  className='border-black border-2 py-2 pl-12 rounded-full w-full focus:outline-2 focus:outline-black focus:outline'
                />
              </div>
            </div>
          </div>
        </form>
      </section>

      <section className='flex-1 flex flex-col justify-between gap-4 px-20 lg:px-40 lg:flex-row'>
        <section className="basis-1/3 flex-none w-1/3">


          <div className="flex flex-1 basis-2/3 flex-col">
            <Header2
              title="Manage Colors"
              alignment='start'
            />

            <div className="py-4 overflow-scroll h-full">
              <ul className="flex flex-col gap-4 items-end">
                {chosenColors.map((color, i) => (
                  <li key={i}
                    className='flex justify-between flex-row gap-4 p-2 items-center w-full rounded-full border-2'
                  >

                    <div
                      style={{backgroundColor: color}}
                      className={`w-10 h-10 rounded-full border-black border-2`}
                    />

                    <p>
                      {color}
                    </p>


                    <button
                      onClick={() => removeColor(color)}
                      className='text-white bg-black p-2 rounded-full h-10 w-10'
                    >
                      <FontAwesomeIcon 
                        icon={faClose}
                      />
                    </button>
                  </li>
                ))}
              </ul>
            </div>

          </div>
        </section>

        <section className="flex flex-col basis-2/3">
          <Header2
            title="Color Combos"
            alignment='end'
          />

          <div className="overflow-scroll w-full border-8 border-black rounded-md">
            <table className='flex flex-col w-full'>
              <tr className="flex flex-row w-full">
                <th className="flex-1 p-4 w-full items-center outline outline-2 outline-black bg-black text-white">
                  Colors
                </th>
                {chosenColors.map((color, i) => (
                  <th key={`foreground-${i}`}
                    className={`flex-1 p-4 w-full items-center outline outline-2 outline-black bg-[${color ? color : '#000000'}]`}
                  >
                    {color}
                  </th>
                ))}
              </tr>

              {chosenColors.map((color, i) => (
                <tr key={`background-${i}`}
                  className="flex flex-row w-full"
                >
                  <td className="flex-1 p-4 w-full text-center outline outline-2 outline-black">
                    {color}
                  </td>
                  {chosenColors.map((c, j) => (
                    <td className={`flex-1 p-4 w-full text-center outline outline-2 outline-black ${color === c && 'bg-black'}`}>
                      {c}/{color}
                    </td>
                  ))}
                </tr>
              ))}
            </table>
          </div>
        </section>
      </section>
    </main>
  );
}

export default App;
